import React, { useState } from "react";
import "./UserProfile.css";
import { useStore } from "../../Store/Store";
import { addBio, uploadProfileImage } from "../../Store/Actions";
import { Icon } from "react-icons-kit";
import CustomButton from "../CustomButton/CustomButton";
import { edit } from "react-icons-kit/feather/edit";
export default function UserProfile(props) {
  const { state, dispatch } = useStore();
  const { user } = state;
  const [isEdit, setIsEdit] = useState(false);
  const [userUploadData, setUserUploadData] = useState({ file: null, bio: "" });
  const defaultProfileImage =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR6Q82WISxpWPp5dHBTWHypFOZbRTvc0ST0xQ&usqp=CAU";

  return (
    <div className="user_profile_container">
      {user && user.full_name && (
        <div className="user_welcome">Welcome&nbsp;{user.full_name}</div>
      )}
      <div className="image_edit_icon">
        <Icon icon={edit} onClick={() => setIsEdit(!isEdit)} />
      </div>
      <div>
        <div className="user_image_container">
          <img
            className="user_image"
            alt="user image"
            src={user && user.img ? user.img : defaultProfileImage}
          />
          <div className="file_upload">
            <br />
            <input
              type="file"
              onChange={(e) => {
                let newFile = e.target.files;

                if (
                  newFile.length > 0 &&
                  newFile[0].size &&
                  newFile[0].type.toString().indexOf("image") > -1
                )
                  setFile(e.target.files[0]);
              }}
            />
          </div>
        </div>{" "}
        <br />
        <br />
        <div className="user_bio">
          {isEdit && (
            <textarea className="user_profile_text_area">
              {user && user.bio}
            </textarea>
          )}
          {!isEdit && <div className="user_bio">{user && user.bio}</div>}
        </div>
      </div>
      <CustomButton text="Submit" />
    </div>
  );
}
