import axios from "../AxiosConfig/AxiosConfig";

export const Login = async function (values, dispatch) {
  axios({
    method: "POST",
    url: "login",
    data: { details: values },
  })
    .then((res) => {
      if (res && res.data && full_name) {
        dispatch({ type: "LOGIN", payload: res.data });
      }
    })
    .catch((e) => {
      //dispatch({ type: "ERROR", payload: e });
    });
};

export const signUp = async function (values, dispatch) {
  axios({
    method: "post",
    url: "signUp",
    data: { details: values },
  })
    .then((res) => {
      console.log(res.data);
    })
    .catch((e) => {
      //dispatch({ type: "ERROR", payload: e });
    });
};

export const uploadProfileImage = async function (file, fileName) {
  const formData = new FormData();
  formData.append("profile_image", file, fileName);
};

export const addBio = async function () {
  alert("shmi");
};

export const getSome = async function () {
