import axios from "../AxiosConfig/AxiosConfig";

export const Login = async function (values, dispatch) {
  axios({
    method: "POST",
    url: "login",
    data: { details: values },
  })
    .then((res) => {
      if (res && res.data && res.data.full_name) {
        dispatch({ type: "LOGIN", payload: res.data });
      }
    })
    .catch((e) => {
      //dispatch({ type: "ERROR", payload: e });
    });
};

export const signUp = async function (values, dispatch) {
  axios({
    method: "post",
    url: "signUp",
    data: { details: values },
  })
    .then((res) => {
      console.log(res.data);
    })
    .catch((e) => {
      //dispatch({ type: "ERROR", payload: e });
    });
};

export const profileUpdate = async function (userUploadData) {
  const formData = new FormData();
  if (userUploadData.file)
    formData.append("profile_image", userUploadData.file);
  if (userUploadData.bio) formData.append("profile_bio", userUploadData.bio);
  //api/updateUserDetails

  axios({
    method: "post",
    url: "updateUserDetails",
    body: userUploadData,
    headers: { "Content-Type": "multipart/form-data" },
  })
    .then((res) => {
      console.log(res.data);
    })
    .catch((e) => {
      //dispatch({ type: "ERROR", payload: e });
    });
};

export const addBio = async function () {
  alert("shmi");
};

export const getSome = async function () {
  axios({
    method: "GET",
    url: "/t",
  });
};
