import axios from "../AxiosConfig/AxiosConfig";

export const Login = async function (values) {
  axios({
    method: "POST",
    url: "addComment",
    data: { user: user, comment: comment },
  })
    .then((res) => {
      if (res && res.data && res.data !== -1) {
        dispatch({ type: "ADD_COMMENT", payload: res.data });
        resolve(1);
        return;
      }
      dispatch({
        type: "ERROR",
        payload: { type: "error", error: "unknown error" },
      });
      resolve(-1);
    })
    .catch((e) => {
      dispatch({ type: "ERROR", payload: e });
      resolve(-1);
    });
};
export const uploadProfileImage = async function (file, fileName) {
  const formData = new FormData();
  formData.append("profile_image", file, fileName);
};

export const addBio = async function () {
  alert("shmi");
};
