import React from "react";
import "./Authentication.css";
import Form from "../Form/Form";
import validations from "../../Validations/Validations";
export default function Authentication(props) {
  return (
    <div className="auth_container">
      {true && (
        <Form
          formLogo={
            <img
              className="authentication_logo"
              src="https://calcalistcareebiz.s3.eu-central-1.amazonaws.com/6288/2/6288636806425463443521_26.jpg"
            />
          }
          fields={{ email: "", full_name: "", password: "" }}
          elements={[
            {
              id: "full_name",
              type: "input",
              title: "Full Name",
              placeholder: "full name",
              customClass: "full_name",
              validate: (v) =>
                validations.first_name(v.toString().toLowerCase()),
              errorMsg: "First name has to be above 2 letters",
            },

            {
              id: "email",
              type: "input",
              title: "Email",
              placeholder: "Email",
              customClass: "email",
              validate: (v) => validations.email(v.toString().toLowerCase()),
              errorMsg: "Email address is incorrect",
            },
            {
              id: "password",
              type: "input",
              title: "Password",
              placeholder: "password",
              customClass: "password",
              validate: (v) =>
                validations.last_name(v.toString().toLowerCase()),
              errorMsg: "password has to be above 2 letters",
            },
          ]}
        />
      )}
      {false && <ConfirmUser />}
    </div>
  );
}

function ConfirmUser(props) {
  return (
    <Form
      formTitle={"Please confirm email code"}
      fields={{ user_confirmaton_code: "" }}
      elements={[
        {
          id: "user_confirmaton_code",
          type: "input",
          title: "Confirmation code",
          placeholder: "code",
          customClass: "confirmation_code",
          validate: (v) => validations.email(v.toString().toLowerCase()),
          errorMsg: "Incorrect confirmation code typed :(",
        },
      ]}
    />
  );
}
