import React, { useEffect } from "react";
import "./Toast.css";

export default function Toast({ error }) {
  const { state, dispatch } = useStore();
  useEffect(() => {
    setTimeout(() => {
      dispatch({ type: "ERROR", payload: -1 });
    }, 5000);
  }, [dispatch]);
  return (
    <div className="toast_container">
      <div className="toast_error_text">{error && error.msg && error.msg}</div>

      <div className="toast_error_type">
        {error && error.type && error.type}
      </div>
    </div>
  );
}
